import React from 'react';
import PersonIcon from '@mui/icons-material/Person';
import CloseIcon from '@mui/icons-material/Close';
import { useHistory, useLocation } from 'react-router-dom';
import '../styles/PostShow.css';

function PostShow() {
  const location = useLocation();
  const history = useHistory();
  const { post } = location.state;

  return (
    <div className='postShow-mainDiv'>
      <div className='postShow-top'>
        <div className='avatar-container'>
          <div style={{marginRight:'5px'}}><PersonIcon/></div>
          <div>Ad Soyad</div>
        </div>
        <button 
          style={{
            backgroundColor:'aliceblue', 
            borderRadius: '50%', 
            border:'none', 
            width:'30px', 
            height:'30px', 
            textAlign:'center', 
            cursor:'pointer'
          }}
          onClick={() => history.goBack()}
        >
          <CloseIcon/>
        </button>
      </div>
      <p className='postShow-status'>{post.status}</p>
      <p className='postShow-adress'>{post.adress}</p>
      {post.image && (
        <div>
          <img src={post.image} alt='Post Image'/>
        </div>
      )}
    </div>
  );
}

export default PostShow;
