import React from 'react';
import PersonIcon from '@mui/icons-material/Person';
import CloseIcon from '@mui/icons-material/Close';
import '../styles/PostShow.css';
import AcildurumButton from './AcildurumButton';
import BagisButton from './BagisButton';
import { useLocation } from "react-router-dom";


const PostShow = ({ post, deletePost }) => {
  const handleHelpClick = () => {
    setTimeout(() => {
      deletePost(post.id); // Assuming deletePost is a function passed as a prop to handle post deletion
    }, 4 * 60 * 60 * 1000); // 4 hours in milliseconds
  };

  const location = useLocation();
  if (post === undefined) {
    post = location?.state?.post;

    if (post === undefined) return (
      <div>not found</div>
    )
  }


  console.log(post)
  let base64String = '';
  if (post.imageBinaryData) {
    base64String = btoa(
      new Uint8Array(post.imageBinaryData).reduce((data, byte) => data + String.fromCharCode(byte), '')
    );
  }

  return (
    <div className='postShow-mainDiv'>
      <div className='postShow-top'>
        <div className='avatar-container'>
          <div style={{ marginRight: '5px' }}><PersonIcon /></div>
          <div>{post.firstName} {post.lastName}</div>
        </div>
        <button style={{ backgroundColor: 'aliceblue', borderRadius: '50%', border: 'none', width: '30px', height: '30px', textAlign: 'center', cursor: 'pointer' }}>
          <CloseIcon />
        </button>
      </div>
      <p className='postShow-status'>{post.email}</p>
      <p className='postShow-adress'>{post.description}</p>
      {post.imageBinaryDataBase64 && (
        <div>
          <img src={`data:image/jpeg;base64,${post.imageBinaryDataBase64}`} alt='Post Image' />
        </div>
      )}
      <div style={{ display: post.emergencyPost ? 'block' : 'none' }}>
        <AcildurumButton onHelpClick={handleHelpClick} />
      </div>
      <div style={{ display: !post.emergencyPost ? 'block' : 'none' }}>
        <BagisButton onHelpClick={handleHelpClick} />
      </div>
    </div>
  );
};

export default PostShow;
