import React from 'react';
import PersonIcon from '@mui/icons-material/Person';
import CloseIcon from '@mui/icons-material/Close';
import '../styles/PostShow.css';

function PostShow({ post }) {
  return (
    <div className='postShow-mainDiv'>
      <div className='postShow-top'>
        <div className='avatar-container'>
          <div style={{ marginRight: '5px' }}><PersonIcon /></div>
          <div>Ad Soyad</div>
        </div>
        <button style={{ backgroundColor: 'aliceblue', borderRadius: '50%', border: 'none', width: '30px', height: '30px', textAlign: 'center', cursor: 'pointer' }}><CloseIcon /></button>
      </div>
      <p className='postShow-status'>{post.status}</p>
      <p className='postShow-adress'>{post.adress}</p>
      {post.image && (
        <div className='postShow-image'>
          <img src={post.image} alt='Post Image' />
        </div>
      )}
    </div>
  );
}

export default PostShow;
