import React, { useState, useEffect } from 'react';
import '../styles/Main.css';
import axios from 'axios';
import PostList from './PostList';
import MenuAppBar from './MenuAppBar';
import MainCard from './MainCard';

function Main() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const email = sessionStorage.getItem("userEmail");
    if (email) {
      // Update the endpoint to include the email parameter
      const endpoint = `http://localhost:8081/api/posts/getAllPosts/${(email)}`;

      axios.get(endpoint)
        .then(response => {
          setPosts(response.data); // Set the received data to the state
        })
        .catch(error => {
          console.error('Error fetching posts:', error);
        });
    } else {
      console.error('No email found in session storage');
    }
  }, []);// Boş dependency array ile sadece bir kez çalışmasını sağlarız

  const createPost = (status, address, image) => {
    const createdPost = [
      ...posts,
      {
        id: Math.round(Math.random() * 9999999),
        status,
        address,
        image
      }
    ];
    setPosts(createdPost);
  };

  return (
    <div className='mainPage'>
      <div className='header'>
        <MenuAppBar />
      </div>
      <div className='mainContent'>
        <div className='sidebar'>
          <MainCard onCreate={createPost} />
        </div>
        <div className='postContainer'>
          <PostList posts={posts} />
        </div>
      </div>
    </div>
  );
}

export default Main;
