import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PersonIcon from '@mui/icons-material/Person';
import "../styles/PostShow.css";
// Import other necessary components

function HelpPosts() {
    const [posts, setPosts] = useState([]);
    const [userDetails, setUserDetails] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPostById = async (postId) => {
            try {
                const response = await axios.get(`http://localhost:8081/api/posts/getPostById/${postId}`);
                return response.data;  // Return individual post data
            } catch (error) {
                console.error('Failed to fetch post details:', error);
                return undefined;  // Continue loading other posts even if one fails
            }
        };

        const fetchUserDetailsAndPosts = async () => {
            try {
                const email = sessionStorage.getItem('userEmail');
                if (!email) {
                    throw new Error('No email found in sessionStorage');
                }

                const userResponse = await axios.get(`http://localhost:8082/api/v1/test/user/${email}`);
                setUserDetails(userResponse.data);  // Save user details for later use in render

                if (!userResponse.data || !userResponse.data.helpToPostlist) {
                    throw new Error('Unexpected API response structure');
                }

                const postIds = userResponse.data.helpToPostlist;
                if (postIds.length === 0) {
                    throw new Error('No posts or empty posts list.');
                }

                const fetchPosts = await Promise.all(postIds.map(fetchPostById));
                setPosts(fetchPosts.filter(post => post !== undefined));
            } catch (error) {
                console.error('Error fetching data:', error);
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchUserDetailsAndPosts();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className='postShow-mainDiv'>
            <div className='postShow-top'>
                <div className='avatar-container'>
                    <PersonIcon style={{ marginRight: '5px' }} />
                    <div>{userDetails.firstName} {userDetails.lastName}</div>
                </div>
            </div>
            <div className='postShow-body'>
                {posts.map((post, index) => (
                    <div key={index}>
                        <p className='postShow-status'>{post.email}</p>
                        <p className='postShow-address'>{post.description}</p>
                        {post.imageBase64 && (
                            <img src={`data:image/jpeg;base64,${post.imageBase64}`} alt='Post' />
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HelpPosts;
