import React, { useEffect, useState } from "react";
import { GoogleMap, LoadScript, Marker } from "@react-google-maps/api";

function Map() {
  const mapContainerStyle = {
    width: "100%",
    height: "90vh",
  };

  const [currentPosition, setCurrentPosition] = useState(null);
  const [locations, setLocations] = useState([]);

  // Fetch current location from sessionStorage
  useEffect(() => {
    const sessionLat = parseFloat(sessionStorage.getItem("latitude"));
    const sessionLng = parseFloat(sessionStorage.getItem("longitude"));
    if (!isNaN(sessionLat) && !isNaN(sessionLng)) {
      setCurrentPosition({ lat: sessionLat, lng: sessionLng });
    }
  }, []);
  

  // Example list of locations (latitude and longitude)
  useEffect(() => {
    // Replace with your actual list fetching logic
    const fetchedLocations = [
      { lat: -34.397, lng: 150.644 },
      { lat: -34.5, lng: 150.8 },
      { lat: -34.3, lng: 150.5 },
    ];
    setLocations(fetchedLocations);
  }, []);

  return (
    <LoadScript googleMapsApiKey="AIzaSyD-sV2HwR7QS2Oni6Jz9IuyPMD448s5TFY">
      <GoogleMap
        mapContainerStyle={mapContainerStyle}
        center={currentPosition || locations[0] || { lat: 0, lng: 0 }}
        zoom={10}
      >
        {/* Render markers */}
        {currentPosition && (
          <Marker
            position={currentPosition}
            icon={{
              url: "http://maps.google.com/mapfiles/ms/icons/blue-dot.png",
            }}
          />
        )}

        {locations.map((location, index) => (
          <Marker
            key={index}
            position={location}
            icon={{
              url: "http://maps.google.com/mapfiles/ms/icons/red-dot.png",
            }}
          />
        ))}
      </GoogleMap>
    </LoadScript>
  );
}

export default Map;
