import React, { useState } from 'react';
import axios from 'axios';
import LogoImage from '../assets/logo.jpeg';
import '../styles/Login.css';
import { useNavigate } from 'react-router-dom';

function Signup() {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [message, setMessage] = useState('');
    const [userId, setUserId] = useState(null);

    const navigate = useNavigate();

    const handleSignup = async (event) => {
        event.preventDefault();
        if (password !== confirmPassword) {
            setMessage('Passwords do not match!');
            return;
        }

        try {
            const response = await axios.post('http://localhost:8082/api/v1/test/register', {
                firstName,
                lastName,
                email,
                password,
            });
            setUserId(response.data.userId);
            console.log(response.data.userId);
            alert(`Signup successful! Your user ID is: ${response.data.userId}`);
            setMessage('Signup successful!');
            navigate('/main');

        } catch (error) {
            setMessage('Signup failed!');
        }
    };

    return (
        <div className='loginDiv'>
            <div className="img-logo" style={{ backgroundImage: `url(${LogoImage})` }}>  </div>
            <h2>Ä°mdat</h2>
            <form onSubmit={handleSignup} className='login-form'>
                <div>
                    <label className='login-label'>First Name: </label>
                    <input
                        type="text"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        className='login-input'
                        placeholder='Please enter your first name'
                    />
                </div>
                <div>
                    <label className='login-label'>Last Name: </label>
                    <input
                        type="text"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        className='login-input'
                        placeholder='Please enter your last name'
                    />
                </div>
                <div>
                    <label className='login-label'>Email: </label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        className='login-input'
                        placeholder='Please enter your email'
                    />
                </div>
                <div>
                    <label className='login-label'>Password: </label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className='login-input'
                        placeholder='Please enter your password'
                    />
                </div>
                <div>
                    <label className='login-label'>Confirm Password: </label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        className='login-input'
                        placeholder='Please confirm your password'
                    />
                </div>
                <button type="submit" className='login-button'>Signup</button>
            </form>
            {message && <p>{message}</p>}
            <div className='link-div'>
                <a href='#' className='signup-link' onClick={() => navigate('/')}>HesabÄ±m var</a>
            </div>
        </div>
    );
}

export default Signup;
