import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CloseIcon from '@mui/icons-material/Close';
import PersonIcon from '@mui/icons-material/Person';
import AddToPhotosIcon from '@mui/icons-material/AddToPhotos';
import '../styles/Post.css';
import { useNavigate } from 'react-router-dom';

function CreatePosts() {
  const [userName, setUserName] = useState('');
  const [userEmail, setUserEmail] = useState('');
  const [status, setStatus] = useState('');
  const [adress, setAdress] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const navigate = useNavigate(); // Initialize the navigate function

  useEffect(() => {
    const storedUserName = sessionStorage.getItem('userName');
    const storedUserEmail = sessionStorage.getItem('userEmail');
    if (storedUserName) {
      setUserName(storedUserName);
    }
    if (storedUserEmail) {
      setUserEmail(storedUserEmail);
    }
  }, []);

  const handleChange = (event) => {
    setStatus(event.target.value);
  }

  const handleAdressChange = (event) => {
    setAdress(event.target.value);
  }

  const handleImageChange = (event) => {
    setImageUrl(event.target.value);
  }

  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent default form submission
    const postData = {
      email: userEmail,
      description: status,
      address: adress,
      imageUrl: imageUrl // Assuming you'll handle image uploading elsewhere
    };

    try {
      const response = await axios.post('http://localhost:8082/api/posts/create', postData);
      console.log('Post created successfully:', response.data);
      setStatus('');
      setAdress('');
      setImageUrl('');
      navigate('/Main'); // Redirect to /Main after successful post creation
    } catch (error) {
      console.error('Error creating post:', error);
    }
  }

  return (
    <div className='createPost'>
      <div className='createPost-top'>
        <h1 className='createPost-maintitle'>Post Oluştur</h1>
        <button className='createPost-topButton'><CloseIcon /></button>
      </div>

      <div className='avatar-container'>
        <div><PersonIcon /></div>
        <div className='avatar-title'>{userName}</div>
      </div>

      <div className='createPost-status'>
        <h4>Durumunuz</h4>
        <input placeholder='Durumunuzu ekleyiniz'
          value={status}
          onChange={handleChange} />
      </div>

      <div className='createPost-adress'>
        <h4>Adresiniz</h4>
        <input placeholder='Adresinizi ekleyiniz'
          type='text'
          value={adress}
          onChange={handleAdressChange} />
      </div>

      <div className='createPost-photo'>
        <div className='createPost-addPhoto'>
          <div><AddToPhotosIcon /></div>
          <input type="text" placeholder="Image URL" value={imageUrl} onChange={handleImageChange} />
          <button style={{ border: 'none', fontSize: '16px', fontWeight: '700', backgroundColor: 'aliceblue' }}>Fotoğraflar/Videolar Ekle</button>
        </div>
      </div>

      <button className='createPost-button' onClick={handleSubmit}>Paylaş</button>
    </div>
  )
}

export default CreatePosts;
