import React, { useState, useEffect } from 'react';
import CloseIcon from '@mui/icons-material/Close';
import PersonIcon from '@mui/icons-material/Person';
import AddToPhotosIcon from '@mui/icons-material/AddToPhotos';
import '../styles/Post.css';
import { Link } from 'react-router-dom';

function CreatePosts({ onCreate }) {
  const [userName, setUserName] = useState('');
  const [status, setStatus] = useState('');
  const [adress, setAdress] = useState('');

  useEffect(() => {
    const storedUserName = sessionStorage.getItem('userName');
    if (storedUserName) {
      setUserName(storedUserName);
    }
  }, []);

  const handleChange = (event) => {
    setStatus(event.target.value);
  }

  const handleAdressChange = (event) => {
    setAdress(event.target.value);
  }

  const handleSubmit = (event) => {
    onCreate(status, adress);
    setStatus('');
    setAdress('');
  }

  return (
    <div className='createPost'>
      <div className='createPost-top'>
        <h1 className='createPost-maintitle'>Post Oluştur</h1>
        <button className='createPost-topButton'><CloseIcon /></button>
      </div>

      <div className='avatar-container'>
        <div><PersonIcon /></div>
        <div className='avatar-title'>{userName}</div>
      </div>

      <div className='createPost-status'>
        <h4>Durumunuz</h4>
        <input placeholder='Durumunuzu ekleyiniz'
          value={status}
          onChange={handleChange} />
      </div>

      <div className='createPost-adress'>
        <h4>Adresiniz</h4>
        <input placeholder='Adresinizi ekleyiniz'
          type='text'
          value={adress}
          onChange={handleAdressChange} />
      </div>

      <div className='createPost-photo'>
        <div className='createPost-addPhoto'>
          <div><AddToPhotosIcon /></div>
          <button style={{ border: 'none', fontSize: '16px', fontWeight: '700', backgroundColor: 'aliceblue' }}>Fotoğraflar/Videolar Ekle</button>
        </div>
      </div>

      <Link to="/Main">
        <button className='createPost-button'
          onClick={handleSubmit}>Paylaş</button>
      </Link>
    </div>
  )
}

export default CreatePosts;
