import React, { useState, useEffect } from 'react';
import '../styles/Main.css';
import axios from 'axios';
import PostList from './PostList';
import MenuAppBar from './MenuAppBar';
import MainCard from './MainCard';

function Main() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    // HTTP isteği yapılacak endpoint
    const endpoint = 'http://localhost:8081/api/posts/getAllPosts';

    axios.get(endpoint)
      .then(response => {
        setPosts(response.data); // Gelen verileri state'e set et
      })
      .catch(error => {
        console.error('Error fetching posts:', error);
      });
  }, []); // Boş dependency array ile sadece bir kez çalışmasını sağlarız

  const createPost = (status, address, image) => {
    const createdPost = [
      ...posts,
      {
        id: Math.round(Math.random() * 9999999),
        status,
        address,
        image
      }
    ];
    setPosts(createdPost);
  };

  return (
    <div className='mainPage'>
      <div>
        <MenuAppBar />
      </div>
      <div>
        <MainCard onCreate={createPost} />
      </div>
      <div>
        <PostList posts={posts} />
      </div>
    </div>
  );
}

export default Main;
