import React, { useEffect, useState } from "react";
import axios from "axios";
import MenuAppBar from "./MenuAppBar";
import "../styles/Map.css"; // Assuming you can reuse the same styles

function HelpPosts() {
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        // Fetch user details
        axios.get(`http://localhost:8082/api/v1/test/user/${userEmail}`)
            .then(response => {
                const { helpToPostList } = response.data;
                // Fetch posts details
                axios.post('http://localhost:8082/api/posts/getPostsByIds', { ids: helpToPostList })
                    .then(postsResponse => {
                        setPosts(postsResponse.data);
                    })
                    .catch(error => console.error('Error fetching posts details:', error));
            })
            .catch(error => console.error('Error fetching user details:', error));
    }, []);
    return (
        <div>
            <MenuAppBar />
            <div className="postsList">
                {posts.map(post => (
                    <div key={post.id}>
                        <h4>{post.status}</h4>
                        <p>{post.address}</p>
                        <img src={post.image} alt="Post" />
                        {/* Different button compared to Map page */}
                        <button onClick={() => handleHelpPostClick(post)}>Help With This</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default HelpPosts;
