import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HelpPosts() {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);



    useEffect(() => {
        const fetchUserDetailsAndPosts = async () => {
            try {
                const email = sessionStorage.getItem('userEmail');
                console.log("this is the email", email);
                const userResponse = await axios.get(`http://localhost:8082/api/v1/test/user/${email}`);
                const postIds = userResponse.data.helpToPostList;
                console.log("these are the ids", postIds);

                if (postIds.length > 0) {
                    const postsResponse = await axios.post('http://localhost:8082/api/posts/getPostsByIds', { ids: postIds });
                    setPosts(postsResponse.data);
                    console.log("postResonse", postsResponse.data)
                } else {
                    setPosts([]); // No posts to display
                }
            } catch (error) {
                console.error('Error fetching data:', error);
                setError('Failed to fetch posts');
            } finally {
                setLoading(false);
            }
        };

        fetchUserDetailsAndPosts();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            {posts.map(post => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    <button onClick={() => {/* Navigate to detailed view */ }}>View Details</button>
                </div>
            ))}
        </div>
    );
}

export default HelpPosts;
