import React from 'react';
import PersonIcon from '@mui/icons-material/Person';
import CloseIcon from '@mui/icons-material/Close';
import '../styles/PostShow.css';

function PostShow({post}) {
  console.log(post)
  return (
    <div className='postShow-mainDiv'>
      <div className='postShow-top'>
        <div className='avatar-container'>
          <div style={{marginRight:'5px'}}><PersonIcon/></div>
          <div>Ad Soyad</div>
        </div>
        <button className='close-button'><CloseIcon/></button>
      </div>
      <p className='postShow-status'>{post.status}</p>
      <p className='postShow-address'>{post.adress}</p>
      {post.image && (
        <div className='postShow-image-container'>
          <img src={post.image} alt='Post Image' className='postShow-image'/>
        </div>
      )}
    </div>
  );
}

export default PostShow;
