import React, { useState, useEffect } from 'react';
import axios from 'axios';

function HelpPosts() {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserDetailsAndPosts = async () => {
            const fetchPostById = async (postId) => {
                try {
                    const response = await axios.get(`http://localhost:8081/api/posts/getPostById/${postId}`);
                    return response.data; // Return individual post data
                } catch (error) {
                    console.error('Failed to fetch post details:', error);
                    // Optionally handle individual post errors, e.g., continue loading other posts
                }
            };
            try {
                const email = sessionStorage.getItem('userEmail');
                if (!email) {
                    console.error('No email found in sessionStorage');
                    setError('User email is not available');
                    setLoading(false);
                    return;
                }

                const userResponse = await axios.get(`http://localhost:8082/api/v1/test/user/${email}`);
                console.log("API Response:", userResponse.data); // Log the entire API response
                console.log("suerResponse.data", userResponse.data);
                console.log("suerResponse.data.helpToPostlist", userResponse.data.helpToPostlist);



                // Early validation of the response
                if (!userResponse.data || !userResponse.data.helpToPostlist) {
                    console.error('Unexpected API response structure:', userResponse.data);
                    setError('Unexpected API response structure');
                    setLoading(false);
                    return;  // Stop processing since the required data is not present
                }

                const postIds = userResponse.data.helpToPostlist;
                console.log("post idss", postIds);
                if (postIds.length === 0) {
                    console.log("No posts or empty posts list.");
                    setPosts([]);
                    setLoading(false);
                    return;
                }

                const postsResponse = await axios.post('http://localhost:8081/api/posts/getPostsById/', { ids: postIds });
                setPosts(postsResponse.data);
            } catch (error) {
                console.error('Error fetching data:', error);
                setError('Failed to fetch posts');
            } finally {
                setLoading(false);
            }
        };

        fetchUserDetailsAndPosts();
    }, []);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            {posts.map(post => (
                <div key={post.id}>
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    <button onClick={() => {/* Navigate to detailed view */ }}>View Details</button>
                </div>
            ))}
        </div>
    );
}

export default HelpPosts;
